import React, { useState } from "react";

function App() {
  const [inputType, setInputType] = useState("");  // Track selected type
  const [file, setFile] = useState(null);          // Store selected file
  const [url, setUrl] = useState("");              // Store URL input
  const [preview, setPreview] = useState("");      // For previewing the image or file

  const handleInputChange = (event) => {
    setInputType(event.target.value);
    setFile(null); // Reset file when the input type changes
    setUrl("");    // Reset URL when the input type changes
    setPreview("");
  };

  // Handle file upload (PDF or Image)
  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);

    // For image preview
    if (inputType === "image") {
      const imageUrl = URL.createObjectURL(selectedFile);
      setPreview(imageUrl);
    }
  };

  // Handle URL input
  const handleUrlChange = (event) => {
    setUrl(event.target.value);
    setPreview(event.target.value);
  };

  return (
    <div>
      <h1>Import File or URL</h1>

      {/* Dropdown for file selection */}
      <label htmlFor="input-type">Select Input Type:</label>
      <select id="input-type" value={inputType} onChange={handleInputChange}>
        <option value="">-- Choose One --</option>
        <option value="pdf">PDF File</option>
        <option value="url">Website URL</option>
        <option value="image">Image File (PNG, JPG, JPEG)</option>
      </select>

      {/* Conditional rendering based on the selected type */}
      {inputType === "pdf" && (
        <div>
          <label htmlFor="pdf-upload">Upload PDF:</label>
          <input type="file" id="pdf-upload" accept="application/pdf" onChange={handleFileChange} />
          {file && <p>PDF Uploaded: {file.name}</p>}
        </div>
      )}

      {inputType === "url" && (
        <div>
          <label htmlFor="url-input">Enter Website URL:</label>
          <input
            type="text"
            id="url-input"
            value={url}
            onChange={handleUrlChange}
            placeholder="https://example.com"
          />
          {url && (
            <div>
              <p>URL Entered: <a href={url} target="_blank" rel="noopener noreferrer">{url}</a></p>
            </div>
          )}
        </div>
      )}

      {inputType === "image" && (
        <div>
          <label htmlFor="image-upload">Upload Image (PNG, JPG, JPEG):</label>
          <input
            type="file"
            id="image-upload"
            accept="image/png, image/jpeg"
            onChange={handleFileChange}
          />
          {preview && (
            <div>
              <p>Image Preview:</p>
              <img src={preview} alt="Preview" width="200" />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
