import React, { useState } from "react";
import { Box, MenuItem, Select, InputLabel, FormControl, Button, TextField, Card, Typography } from "@mui/material";
import axios from 'axios';

function App() {
  const [inputType, setInputType] = useState("");  // Track selected type
  const [file, setFile] = useState(null);          // Store selected file
  const [url, setUrl] = useState("");              // Store URL input
  const [preview, setPreview] = useState("");      // For previewing the image or file

  const handleInputChange = (event) => {
    setInputType(event.target.value);
    setFile(null); // Reset file when the input type changes
    setUrl("");    // Reset URL when the input type changes
    setPreview("");
  };

  // Handle file upload (PDF, Image, or Text)
  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);

    // For image preview
    if (inputType === "image") {
      const imageUrl = URL.createObjectURL(selectedFile);
      setPreview(imageUrl);
    }

    // For text file preview
    if (inputType === "txt") {
      const reader = new FileReader();
      reader.onload = () => setPreview(reader.result);
      reader.readAsText(selectedFile);
    }

    // For PDF file preview (optional)
    if (inputType === "pdf") {
      const reader = new FileReader();
      reader.onload = () => {
        setPreview("PDF file uploaded: " + selectedFile.name);
      };
      reader.readAsArrayBuffer(selectedFile);
    }
  };

  // Handle URL input
  const handleUrlChange = (event) => {
    setUrl(event.target.value);
    setPreview(event.target.value);
  };

  // Handle the Generate button click
  const handleGenerate = async () => {
    if (inputType === "pdf" && file) {
      const formData = new FormData();
      formData.append("pdf", file);

      try {
        const response = await axios.post("http://localhost:5000/upload-pdf", formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        alert(`PDF file "${file.name}" uploaded successfully.`);
        console.log("Response:", response.data);
      } catch (error) {
        console.error("Error uploading PDF:", error);
        alert("Failed to upload the PDF.");
      }
    } else if (inputType === "url" && url) {
      try {
        const response = await axios.post("http://localhost:5000/scrape", { url });

        const blob = new Blob([response.data], { type: "text/plain" });
        const link = document.createElement("a");
        link.href = window.URL.createObjectURL(blob);
        link.download = "scraped_data.txt";
        link.click();
      } catch (error) {
        console.error("Error scraping the URL:", error);
        alert("Failed to scrape the URL.");
      }
    } else if (inputType === "image" && file) {
      const formData = new FormData();
      formData.append("image", file);

      try {
        const response = await axios.post("http://localhost:5000/extract-text", formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        alert(`Image "${file.name}" uploaded successfully.`);
        console.log("Extracted Text:", response.data);
      } catch (error) {
        console.error("Error uploading image:", error);
        alert("Failed to upload the image.");
      }
    } else if (inputType === "txt" && file) {
      const formData = new FormData();
      formData.append("txt", file);

      try {
        const response = await axios.post("http://localhost:5000/upload-txt", formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        alert(`Text file "${file.name}" uploaded successfully.`);
        console.log("Response:", response.data);
      } catch (error) {
        console.error("Error uploading TXT:", error);
        alert("Failed to upload the text file.");
      }
    } else {
      alert("Please upload a file or enter a URL.");
    }
  };

  // New handleCreate function
  const handleCreate = async () => {
    try {
      const response = await axios.post("http://localhost:5001/process-file");
      alert(response.data);
    } catch (error) {
      console.error("Error creating summary:", error);
      alert("Failed to create summary.");
    }
  };

  return (
    <Box sx={{ padding: 4, maxWidth: 600, margin: 'auto', mt: 4 }}>
      <Card variant="outlined" sx={{ padding: 3 }}>
        <Typography variant="h4" gutterBottom>
          Import File or URL
        </Typography>

        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Input Type</InputLabel>
          <Select
            value={inputType}
            label="Select Input Type"
            onChange={handleInputChange}
          >
            <MenuItem value="">-- Choose One --</MenuItem>
            <MenuItem value="pdf">PDF File</MenuItem>
            <MenuItem value="url">Website URL</MenuItem>
            <MenuItem value="image">Image File (PNG, JPG, JPEG)</MenuItem>
            <MenuItem value="txt">Text File (.txt)</MenuItem>
          </Select>
        </FormControl>

        {inputType === "url" && (
          <Box>
            <TextField
              label="Enter Website URL"
              variant="outlined"
              fullWidth
              value={url}
              onChange={handleUrlChange}
              placeholder="https://example.com"
              sx={{ mb: 2 }}
            />
            {url && (
              <Typography>
                URL Entered: <a href={url} target="_blank" rel="noopener noreferrer">{url}</a>
              </Typography>
            )}
          </Box>
        )}

        {inputType === "image" && (
          <Box>
            <input
              accept="image/*"
              type="file"
              onChange={handleFileChange}
              style={{ margin: '10px 0' }}
            />
            {preview && <img src={preview} alt="Preview" style={{ width: '100%', height: 'auto' }} />}
          </Box>
        )}

        {inputType === "pdf" && (
          <Box>
            <input
              accept="application/pdf"
              type="file"
              onChange={handleFileChange}
              style={{ margin: '10px 0' }}
            />
            {preview && <Typography>{preview}</Typography>}
          </Box>
        )}

        {inputType === "txt" && (
          <Box>
            <input
              accept=".txt"
              type="file"
              onChange={handleFileChange}
              style={{ margin: '10px 0' }}
            />
            {preview && <Typography>File Preview: {preview}</Typography>}
          </Box>
        )}

        <Box sx={{ mt: 4 }}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            onClick={handleGenerate}
          >
            Generate
          </Button>
          {/* Add the Create button here */}
          <Button
            variant="outlined"
            color="secondary"
            fullWidth
            sx={{ mt: 2 }}
            onClick={handleCreate} // Use the new handleCreate function
          >
            Create
          </Button>
        </Box>
      </Card>
    </Box>
  );
}

export default App;
