import React, { useState } from "react";
import { Box, MenuItem, Select, InputLabel, FormControl, Button, TextField, Card, Typography, CircularProgress } from "@mui/material";
import axios from 'axios';
import { getStorage, ref, getDownloadURL } from "firebase/storage";


function MainApp() {
  const [inputType, setInputType] = useState("");
  const [file, setFile] = useState(null);
  const [url, setUrl] = useState("");
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);  // Loading state
  const [errorMessage, setErrorMessage] = useState("");  // Error message
  const [message, setMessage] = useState('');
  const [showUrlButton, setShowUrlButton] = useState(false); 
  const [showDownloadButton, setShowDownloadButton] = useState(false);
  const [showSummaryButton, setShowSummaryButton] = useState(false); 
  const [summary,setSummary] = useState("This is a default summary. A summary will appear here after generating one.");

  const handleInputChange = (event) => {
    setInputType(event.target.value);
    setFile(null);
    setUrl("");
    setPreview("");
    setErrorMessage("");  // Reset error message
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
    setErrorMessage("");  // Reset error message

    if (inputType === "image") {
      const imageUrl = URL.createObjectURL(selectedFile);
      setPreview(imageUrl);
    } else if (inputType === "txt") {
      const reader = new FileReader();
      reader.onload = () => setPreview(reader.result);
      reader.readAsText(selectedFile);
    } else if (inputType === "pdf") {
      const reader = new FileReader();
      reader.onload = () => {
        setPreview("PDF file uploaded: " + selectedFile.name);
      };
      reader.readAsArrayBuffer(selectedFile);
    }
  };

  const handleUrlChange = (event) => {
    setUrl(event.target.value);
    setPreview(event.target.value);
    setErrorMessage("");  // Reset error message
  };

  const handleGenerate = async () => {
    setLoading(true);  // Set loading state
    setErrorMessage("");  // Reset error message

    try {
      if (inputType === "pdf" && file) {
        const formData = new FormData();
        formData.append("pdf", file);
        await axios.post("http://localhost:5000/upload-pdf", formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        alert(`PDF file "${file.name}" uploaded successfully.`);

      } else if (inputType === "url" && url) {
        const response = await axios.post("http://localhost:5000/scrape", { url });
        const blob = new Blob([response.data], { type: "text/plain" });
        const link = document.createElement("a");
        link.href = window.URL.createObjectURL(blob);
        link.download = "scraped_data.txt";
        link.click();
      } else if (inputType === "image" && file) {
        const formData = new FormData();
        formData.append("image", file);
        await axios.post("http://localhost:5000/extract-text", formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        alert(`Image "${file.name}" uploaded successfully.`);

      } else if (inputType === "txt" && file) {
        const formData = new FormData();
        formData.append("txt", file);
        await axios.post("http://localhost:5000/upload-txt", formData, { headers: { 'Content-Type': 'multipart/form-data' } });
        alert(`Text file "${file.name}" uploaded successfully.`);
      } else {
        alert("Please upload a file or enter a URL.");
      }
    } catch (error) {
      console.error("Error:", error);
      setErrorMessage("Failed to process your request. Please try again.");
    } finally {
      setLoading(false);  // Reset loading state
      setInputType("");  // Reset input type
      setFile(null);  // Reset file
      setUrl("");  // Reset URL
      setPreview("");  // Reset preview
    }
  };

  
  const handleCreate = async () => {
    setLoading(true);  // Set loading state
    setErrorMessage("");  // Reset error message

    try {
        const response = await axios.post("http://localhost:5000/run-process-file");
        setSummary(response.data);
         // Show success message or output
        setShowSummaryButton(true);

    } catch (error) {
        console.error("Error:", error);
        setErrorMessage("Failed to run the process. Please try again.");
    } finally {
        setLoading(false);  // Reset loading state
    }
};


const handleDrawMindMap = async () => {
  setLoading(true);
  setErrorMessage("");

  try {
    const response = await axios.post("http://localhost:5000/generate-mind-map");
    alert(response.data.message);  // Show success message or output
    setShowUrlButton(true);
    setShowDownloadButton(true);
  } catch (error) {
    console.error("Error:", error);
    setErrorMessage("Failed to generate mind map. Please try again.");
  } finally {
    setLoading(false);
  }
};

/*const handleViewSummary = async () => {
  setLoading(true);
  setErrorMessage("");

  try {
    const response = await axios.get("http://localhost:5000/get-summary");
    setSummary(response.data);
    // You can remove the alert line to avoid popups
  } catch (error) {
    console.error("Error:", error);
    setErrorMessage("Failed to retrieve summary. Please try again.");
  } finally {
    setLoading(false);
  }
};*/

// Call this function on the appropriate event, like onClick



  return (
    <>
    <header className="header">
        <h1 className="logo">MindMesh</h1>
      </header>
    <Box sx={{ display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh', // Ensure it takes the full height of the viewport
    padding: 4, 
    maxWidth: 800, 
    margin: 'auto', }}>
      <Card variant="outlined" sx={{ padding: 7,borderRadius: 4, // Adjust the value for more or less rounding
    boxShadow: 2, }}>
        <Typography variant="h4" gutterBottom>
        Attach Your File
        </Typography>
        {errorMessage && <Typography color="error">{errorMessage}</Typography>}  {/* Show error message */}
        
        <FormControl fullWidth sx={{ mb: 3 }}>
          <InputLabel>Select Input Type</InputLabel>
          <Select value={inputType} label="Select Input Type" onChange={handleInputChange}>
            <MenuItem value="">-- Choose One --</MenuItem>
            <MenuItem value="pdf">PDF File</MenuItem>
            <MenuItem value="url">Website URL</MenuItem>
            <MenuItem value="image">Image File (PNG, JPG, JPEG)</MenuItem>
            <MenuItem value="txt">Text File (.txt)</MenuItem>
          </Select>
        </FormControl>

        {inputType === "url" && (
          <Box>
            <TextField label="Enter Website URL" variant="outlined" fullWidth value={url} onChange={handleUrlChange} placeholder="https://example.com" sx={{ mb: 2 }} />
            {url && <Typography>URL Entered: <a href={url} target="_blank" rel="noopener noreferrer">{url}</a></Typography>}
          </Box>
        )}

        {inputType === "image" && (
          <Box>
            <input accept="image/*" type="file" onChange={handleFileChange} style={{ margin: '10px 0' }} />
            {preview && <img src={preview} alt="Preview" style={{ width: '100%', height: 'auto' }} />}
          </Box>
        )}

        {inputType === "pdf" && (
          <Box>
            <input accept="application/pdf" type="file" onChange={handleFileChange} style={{ margin: '10px 0' }} />
            {preview && <Typography>{preview}</Typography>}
          </Box>
        )}

        {inputType === "txt" && (
          <Box>
            <input accept=".txt" type="file" onChange={handleFileChange} style={{ margin: '10px 0' }} />
            {preview && <Typography>File Preview: {preview}</Typography>}
          </Box>
        )}

        <Box sx={{ mt: 4 }}>
          <Button variant="outlined" color="secondary" fullWidth onClick={handleGenerate} disabled={loading}>
            {loading ? <CircularProgress size={24} /> : "Load Data"}
          </Button>
        
          {/* Add the Create button here */}
          <Button variant="outlined" color="secondary" fullWidth sx={{ mt: 2 }} onClick={handleCreate}>
           Generate Summary
          </Button>
          */{showSummaryButton&&(
            <><Button variant="outlined" color="secondary" fullWidth sx={{ mt: 2 }} onClick={handleViewSummary}>view summary</Button></>
            )}*/
           <Button variant="outlined" color="secondary" fullWidth sx={{ mt: 2 }} onClick={handleDrawMindMap} disabled={loading}>
          draw mind map
          </Button>
          {showUrlButton && (
            
            <Button variant="outlined" color="secondary" fullWidth sx={{ mt: 2 }} href="https://firebasestorage.googleapis.com/v0/b/mindmapwebsitepdf.appspot.com/o/mindmap%2Fmind_map.png?alt=media&token=f9d85688-56e2-4150-b081-53731fbabd96" target="_blank">
              View Mind Map
            </Button>
            
          
          )}
        </Box>
       
        
      </Card>

      
    </Box>
    {summary && (
  <Box 
    sx={{ 
      mt: 4, 
      p: 2, 
      border: "1px solid #ccc", 
      backgroundColor: 'white',
      maxWidth: '800px', 
      margin: '20px auto', 
      overflowX: 'auto' , // Enable horizontal scrolling
      borderRadius: 4,// Adjust the value for more or less rounding
    boxShadow: 2
    }}
  >
    <Typography variant="h6">Summary:</Typography>
    <Typography>
      <pre>{summary}</pre>
    </Typography>
  </Box>
)}

    </>
  );
}

export default MainApp;
